	def flag_current_song_done(self):
		# print("flag called, songNum = " + str(self.songNum))
		row = self.songNum
		self.song_pool[row][1] = 1

	def get_current_track(self):
		print(self.track)
		return self.track

	def next_track(self, action):
		# print(self.song_pool[self.songNum])
		self.flag_current_song_done()
		if action == 'finish':

			currentSongNum = self.songNum
			row = self.songNum
			thresh1 = (self.AVDIFF/3)

			for i in range(len(self.prob_matrix[row])):
				if row == i:
					continue
				elif self.song_pool[i][1] == 0:
					if self.prob_matrix[row][i] < thresh1:
						print(self.prob_matrix[row][i])  #this is the difference value of the selected song
						self.track = self.song_pool[i][0]
						self.songNum = i
						break

			if self.songNum == currentSongNum:
				temp = self.prob_matrix[row]
				temp[row] = 100000
				self.songNum = np.argmin(temp)
				print(self.prob_matrix[row][self.songNum])  # this is the difference value of the selected song


		elif action == 'like':

			currentSongNum = self.songNum
			row = self.songNum
			thresh = (self.AVDIFF/4)

			for i in range(len(self.prob_matrix[row])):
				if row == i:
					continue
				elif self.song_pool[i][1] == 0:
					print(self.prob_matrix[row][i])  # this is the difference value of the selected song
					if self.prob_matrix[row][i] < thresh:
						self.track = self.song_pool[i][0]
						self.songNum = i
						break

			if self.songNum == currentSongNum:
				temp = self.prob_matrix[row]
				temp[row] = 100000
				self.songNum = np.argmin(temp)
				print(self.prob_matrix[row][self.songNum])  # this is the difference value of the selected song

		elif action == 'dislike':

			currentSongNum = self.songNum
			row = self.songNum
			thresh1 = (self.AVDIFF/2)

			for i in range(len(self.prob_matrix[row])):
				if row == i:
					continue
				elif self.song_pool[i][1] == 0:
					print(self.prob_matrix[row][i])  # this is the difference value of the selected song
					if self.prob_matrix[row][i] > thresh1:
						self.track = self.song_pool[i][0]
						self.songNum = i
						break

			if self.songNum == currentSongNum:
				temp = self.prob_matrix[row]
				temp[row] = -100000
				self.songNum = np.argmax(temp)
				print(self.prob_matrix[row][self.songNum])  # this is the difference value of the selected song

		elif action == 'skip':

			currentSongNum = self.songNum
			row = self.songNum
			thresh1 = (self.AVDIFF)

			for i in range(len(self.prob_matrix[row])):
				if row == i:
					continue
				elif self.song_pool[i][1] == 0:
					if self.prob_matrix[row][i] > thresh1:
						print(self.prob_matrix[row][i])  # this is the difference value of the selected song
						self.track = self.song_pool[i][0]
						self.songNum = i
						break

			if self.songNum == currentSongNum:
				temp = self.prob_matrix[row]
				temp[row] = 100000
				self.songNum = np.argmax(temp)
				print(self.prob_matrix[row][self.songNum])  # this is the difference value of the selected song

	def avDiff(self):
		## avDiff is the same for all rows
		row = 0
		tot = 0

		for i in range(len(self.prob_matrix[row])):
			tot = tot + self.prob_matrix[row][i]
		return tot/len(self.prob_matrix)
